var modalMessage=document.querySelector(".modal-content"),modalMessageNameField=modalMessage.querySelector("[name=user-name]"),modalMessageOpenBtn=document.querySelector(".btn-feedback");modalMessageOpenBtn.addEventListener("click",function(e){e.preventDefault(),modalMessage.classList.add("modal-content-show"),modalMessageNameField.focus()});var modalMessageCloseBtn=modalMessage.querySelector(".modal-content-close");modalMessageCloseBtn.addEventListener("click",function(e){e.preventDefault(),modalMessage.classList.remove("modal-content-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalMessage.classList.contains("modal-content-show")&&modalMessage.classList.remove("modal-content-show")});